import { useEffect, useState } from "react"
import { Link, useLocation } from "react-router-dom"
import Loader from "../layout/loader"
import Message from "../layout/message"
import Styles from './Contas.module.css'


function Contas(){

    const location=useLocation()
    const [contas,setContas]=useState()

    let message=''
    let messageExclusao=''
    let messageDeMenorDeIdade=''
    let messageDeFaltaDeInformacao=''
    let messageCpfInvalido=''
    let messageDataInvalido=''

    if(location.state){
        message=location.state.messageCriacao
        messageExclusao=location.state.messageExclusao
        messageDeMenorDeIdade=location.state.messageDeMenorIdade
        messageDeFaltaDeInformacao=location.state.messageDeFaltaDeInformacao
        messageCpfInvalido=location.state.messageCpfInvalido
        messageDataInvalido=location.state.messageDataInvalido
        
    }

    useEffect(()=>{
        fetch('http://localhost:5000/contas',{
            method:'GET',
            headers:{
                'Content-Type':'application/json'
            }
        }).then(resp=>resp.json()).then(data=>setContas(data))
    },[])

    return(
        <>
        {!contas && (<Loader/>)}
        {messageExclusao &&(<Message message={messageExclusao} type='sucesso'/>)}
        {message &&(<Message message={message} type='sucesso'/>)}
        {messageDeMenorDeIdade && ( <Message message={messageDeMenorDeIdade} type='error'/>)}
        {messageDeFaltaDeInformacao && ( <Message message={messageDeFaltaDeInformacao} type='error'/>) }
        {messageCpfInvalido && (<Message message={messageCpfInvalido} type='error'/>)}
        {messageDataInvalido && (<Message message={messageDataInvalido} type='error'/>)}

        <div className={Styles.titulo}>
            <h1>Contas</h1>
            <Link to='/NewConta'><button>Criar conta</button></Link>
        </div>

        <div className={Styles.conta}>
        {contas && contas.map((conta,index)=>(
        <Link  key={index} className={Styles.contaCard} to={`/Conta/${conta.id}`}>
            <div >
                <h2>{conta.nomeConta}</h2>
                <p><span>Propriet√°rio:</span> {conta.nomeProprietario}</p>
                <p><span>Saldo:</span> R${conta.saldo}</p>
                <p><span>CPF:</span> {conta.cpfProprietario}</p>
                <p><span>Data de nascimento:</span> {conta.dataNascimento}</p>
                <p><span>Tipo Conta:</span> {conta.tipoConta.tipo}</p>
                <p className={Styles.aviso}>clique para abrir</p>
            </div>
        </Link>
        ))}

        </div>
        </>
  
     
    )
}
export default Contas
